INSERT INTO `article` VALUES ('1', '管理员', '\0', '实验室', '实验室介绍', '<p style=\"margin-top:10px;margin-right:28px;margin-bottom:10px;margin-left:0;text-align:center;line-height:200%\"><strong><span style=\"font-size:24px;line-height:200%;font-family:黑体\">实验室介绍</span></strong></p><p style=\"margin-right: 28px; text-indent: 28px; line-height: 35px; text-align: left;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"font-size: 16px;\">西南野生动植物资源保护教育部重点实验室是</span><span style=\"font-size: 16px;\">2008</span><span style=\"font-size: 16px;\">年教育部批准的重点实验室。该实验室的宗旨是为实现野生动植物的可持续利用，加强对濒危物种及其生境的保护，促进人类社会和谐稳定的发展，为我国、特别是为西南地区野生动植物资源研究与保护事业提供理论、技术支持和人才培养平台。</span></span></p><p style=\"text-align:center\"><img src=\"/Upload/image/20161115/6361481540592874666893004.jpg\" alt=\"6361481540592874666893004.jpg\" width=\"243\" height=\"172\"/></p><p style=\"margin-right: 28px; text-indent: 28px; line-height: 35px; text-align: right;\"><span style=\"font-size:16px;font-family:仿宋\"></span><br/></p><p style=\";text-indent: 28px;line-height: 35px\"><span style=\"font-size: 16px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">实验室以省高校重点实验室“环境科学与生物多样性保护实验室”、省级重点资助建设的重点学科“野生动植物保护与利用”和省级重点学科“动物学”以及四川省高校实验教学示范中心“动物学实验室”等研究机构为主要支撑，以“开放、流动、联合、竞争”为建设方针，继续发挥优势和特色，坚持加强基础研究与着力应用研究的建设思路，采用资源研究与保护事业相结合的思路，努力成为在国际上有一定影响的开放性、多学科、综合性的动植物资源保护研究国际交流与合作平台。</span></p><p>&nbsp;</p><p><br/></p><p><br/><br/></p>', '', '0', 'jbxx', '基本信息', 'sysjs', '实验室介绍', '10000', '3', '2016-11-15 00:00:00', '');
INSERT INTO `article` VALUES ('2', '管理员', '', '实验室', '昆虫学家、中国科学院动物研究所博士生导师黄大卫研究员来我校讲学', '<p style=\"text-indent: 28px\"><span style=\";font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">7</span><span style=\";font-family:宋体\">月</span><span style=\";font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">19</span><span style=\";font-family:宋体\">日晚上</span><span style=\";font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">8:00</span><span style=\";font-family:宋体\">，昆虫学家、中国昆虫学会副理事长、秘书长、中国科学院动物研究所原所长、博士生导师黄大卫研究员应邀来到生命科学学院作了题为</span><span style=\";font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">“</span><span style=\";font-family:宋体\">榕小蜂从基因到基因组的跨域研究：皆因形态分类而起</span><span style=\";font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">”</span><span style=\";font-family:宋体\">的学术报告。报告会由生命科学学院党委书记唐赟主持，我校生命科学学院隐翅虫科分类专家郑发科教授，部分老师、研究生和本科生等</span><span style=\";font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">40</span><span style=\";font-family:宋体\">余人聆听了此次报告。</span></p><p style=\"text-indent: 28px\"><span style=\";font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">“</span><span style=\";font-family:宋体\">小榕果大世界，直径不超过</span><span style=\";font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">4cm</span><span style=\";font-family:宋体\">的榕果容纳了</span><span style=\";font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">30</span><span style=\";font-family:宋体\">多种榕小蜂。</span><span style=\";font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">”</span><span style=\";font-family:宋体\">黄大卫研究员从榕果与榕</span> <span style=\";font-family:宋体\">小蜂间互利进化关系开始，讲述了课题组在榕小蜂极端的两性异形、物种分化、种类的</span><span style=\";font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">DNA</span><span style=\";font-family:宋体\">条形码简单等诸多科学问题上的新发现。从形态到分子，从分类到生</span> <span style=\";font-family:宋体\">态，黄大卫研究员关于榕小蜂和榕果间有趣的生物学故事极大的激发了在座师生的科研兴趣。</span></p><p style=\"text-indent: 28px\"><span style=\";font-family:宋体\">报告结束后，黄大卫研究员与生命科学学院师生亲切互动，现场气氛热烈融洽，师生获益匪浅。</span></p><p style=\"text-align: center;\"><img src=\"/Upload/image/20161115/6361481746915570765401848.jpg\" title=\"昆虫学家、中国科学院动物研究所博士生导师黄大卫研究员来我校讲学.jpg\" alt=\"昆虫学家、中国科学院动物研究所博士生导师黄大卫研究员来我校讲学.jpg\"/></p>', '/Upload/article/20161115/7dfc4d9c874e452bb110d7ed6d736a5f.jpg', '0', 'tzgg', '通知公告', 'zxdt', '最新动态', '10000', '3', '2016-07-24 00:00:00', '');
