INSERT INTO `article` VALUES ('6', '管理员', '\0', '实验室', '仪器设备', '<p style=\"text-align:center\"><strong><span style=\"font-size:20px;font-family:宋体\">仪器设备</span></strong></p><p style=\"text-indent:24px\"><span style=\"font-size:16px\">&nbsp;</span></p><p style=\"text-indent:40px;line-height:150%\"><span style=\"font-size:16px;line-height:150%;font-family: 宋体\">本实验室自</span><span style=\"font-size:16px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">2008</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">年由教育部批准建设以来，</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">根据实验室各研究方向发展的需要，主要购置了：成分、结构分析仪器（用于分析生化物质组成和含量），物质分离和纯化仪器（用于被测样品的提纯），蛋白质和基因组成分析和测定仪器（用于测定动植物体内关键蛋白质和功能基因的组成成分），植物光合作用等生理指标测定系统（用于测定植物光合作用、蒸腾作用、呼吸作用等基本生理过程等），动物行为活动分析设备（用于研究野生动物的日常行为活动分析），以及一些与分析和测定相关的常规大型配套设备等。</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">目前，实验室已具备了从宏观到微观的系列研究设备，</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">主要仪器设备如下：</span></p><p style=\"text-indent:28px\"><span style=\"font-family:宋体\">&nbsp;</span></p><table cellspacing=\"0\" cellpadding=\"0\"><tbody><tr class=\"firstRow\"><td width=\"191\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762667174674570323.jpg\" title=\"clip_image002.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">DNA</span><span style=\"font-size:12px;font-family:宋体\">遗传分析系统</span></p></td><td width=\"191\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762675330657014721.jpg\" title=\"clip_image004.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">AKTA</span><span style=\"font-size:12px;font-family:宋体\">蛋白纯化仪</span></p></td><td width=\"185\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762683749994757291.jpg\" title=\"clip_image006.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:宋体\">流式细胞仪</span></p></td></tr><tr><td width=\"191\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762691906411027279.jpg\" title=\"clip_image008.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:宋体\">高效液相色谱仪</span></p></td><td width=\"191\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762700263303471678.jpg\" title=\"clip_image010.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:宋体\">气相色谱与质谱联用仪</span></p></td><td width=\"185\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762708685131214248.jpg\" title=\"clip_image012.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:宋体\">酶标仪</span></p></td></tr><tr><td width=\"191\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762717056236484236.jpg\" title=\"clip_image014.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:宋体\">脂肪测定仪</span></p></td><td width=\"191\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762725419878928635.jpg\" title=\"clip_image016.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:宋体\">氨基酸分析仪</span></p></td><td width=\"185\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762733696426671205.jpg\" title=\"clip_image018.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:宋体\">双色红外激光成像系统</span></p></td></tr><tr><td width=\"191\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762741920662941193.jpg\" title=\"clip_image020.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:宋体\">全自动凯氏定氮仪</span></p></td><td width=\"191\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762750353075385592.jpg\" title=\"clip_image022.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:宋体\">全封闭式组织脱水机</span></p></td><td width=\"185\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762758707473128162.jpg\" title=\"clip_image024.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:宋体\">半自动纤维分析仪</span></p></td></tr><tr><td width=\"191\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762767065518398150.jpg\" title=\"clip_image026.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:宋体\">多功能染色机</span></p></td><td width=\"191\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762775285371842549.jpg\" title=\"clip_image028.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:宋体\">实时荧光定量</span><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">PCR</span><span style=\"font-size:12px;font-family:宋体\">仪</span></p></td><td width=\"185\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762783141628585119.jpg\" title=\"clip_image030.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:宋体\">全自动间断化学分析仪</span></p></td></tr><tr><td width=\"191\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762792031124855107.jpg\" title=\"clip_image032.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:宋体\">原子吸收分光光度计</span></p></td><td width=\"191\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762800287587299506.jpg\" title=\"clip_image034.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:宋体\">碳氮分析仪</span></p></td><td width=\"185\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762808522715042076.jpg\" title=\"clip_image036.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:宋体\">根系分析系统</span></p></td></tr><tr><td width=\"191\" valign=\"top\" style=\"padding: 0px 7px;\"><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762816986941312064.jpg\" title=\"clip_image038.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:宋体\">调制叶绿素荧光成像系统</span></p></td><td width=\"191\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762825314223756462.jpg\" title=\"clip_image040.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:宋体\">植物根系生长监测系统</span></p></td><td width=\"185\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762833790781499033.jpg\" title=\"clip_image042.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:宋体\">便携式光合作用仪</span></p></td></tr><tr><td width=\"191\" valign=\"top\" style=\"padding: 0px 7px;\"><p><span style=\"font-size:12px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><img src=\"/Upload/image/20161116/6361489762842139066769021.jpg\" title=\"clip_image044.jpg\"/></span></p><p style=\"text-align:center\"><span style=\"font-size:12px;font-family:宋体\">多参数水质监测仪</span></p></td><td width=\"191\" valign=\"top\" style=\"padding: 0px 7px;\"><br/></td><td width=\"185\" valign=\"top\" style=\"padding: 0px 7px;\"><br/></td></tr></tbody></table><p style=\"text-align:center\"><span style=\"font-family:宋体\">每张仪器图片和仪器名称设为可链接。点击链接如下：</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;text-align: center;text-indent:0\"><strong><span style=\"font-size:19px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">DNA</span></strong><strong><span style=\"font-size:19px;font-family: 宋体\">遗传分析系统</span></strong></p><p class=\"MsoListParagraph\" style=\"margin-left: 24px;text-indent: 0\"><img src=\"/Upload/image/20161116/6361489762850368519213419.jpg\" hspace=\"12\" title=\"clip_image046.jpg\"/></p><p class=\"MsoListParagraph\" style=\"margin-left: 24px;text-indent: 0;line-height: 200%\"><strong><span style=\"font-family:宋体\">仪器型号</span></strong><span style=\"font-family:宋体\">：</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">LI-COR 4300</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;text-indent:0;line-height:200%\"><strong><span style=\"font-family:宋体\">生产厂家</span></strong><span style=\"font-family: 宋体\">：美国</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">LI-COR</span><span style=\"font-family:宋体\">公司</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;text-indent:0;line-height:200%\"><strong><span style=\"font-family:宋体\">购置年份</span></strong><span style=\"font-family: 宋体\">：</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">2012</span><span style=\"font-family:宋体\">年</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;text-indent:0;line-height:200%\"><strong><span style=\"font-family:宋体\">存放地点：</span></strong><span style=\"font-family: 宋体\">重点实验室</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">407</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;text-indent:0\"><strong><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></strong></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;text-indent:0\"><strong><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></strong></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;text-indent:0\"><strong><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></strong></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;text-indent:0\"><strong><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></strong></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;text-indent:0\"><strong><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></strong></p><p class=\"MsoListParagraph\" style=\"margin-top:0;margin-right:0;margin-bottom: 10px;margin-left:24px;text-indent:0\"><strong><span style=\"font-family:宋体\">仪器简介：</span></strong></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;line-height:150%\"><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">Li-Cor 4300 DNA</span><span style=\"font-family:宋体\">分析系统是在</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">LI-COR </span><span style=\"font-family:宋体\">特有的高灵敏度近红外荧光检测技术基础上发展起来的第三代</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">DNA</span><span style=\"font-family:宋体\">分析系统，是进行遗传分析和序列测定工作的理想工具。</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;line-height:150%\"><span style=\"font-family:宋体\">在</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">AFLP</span><span style=\"font-family:宋体\">应用方面，该仪器除可以进行传统的基因组</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">AFLP</span><span style=\"font-family:宋体\">分析外，还可以做表达性</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">cDNA</span><span style=\"font-family:宋体\">的</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">AFLP</span><span style=\"font-family:宋体\">分析。另外，结合</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">LI-COR </span><span style=\"font-family:宋体\">的红外荧光扫描成像系统</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">Odyssey</span><span style=\"font-family:宋体\">，可进行</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">AFLP</span><span style=\"font-family:宋体\">多态性位点的片段回收，便于进行进一步的基因功能研究；在微卫星分析（</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">STR</span><span style=\"font-family:宋体\">）方面，</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">LI-COR</span><span style=\"font-family:宋体\">公司提供有功能强大的</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">SAGAGT</span><span style=\"font-family:宋体\">分析软件，其独特的</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">Allele Fingerprinting </span><span style=\"font-family:宋体\">算法保证了分析的准确性，对复杂的两碱基重复也可准确分析。该软件自动化程度高，分析速度快；</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">LI</span><span style=\"font-family:宋体\">技术的发展，</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">LI-COR4300</span><span style=\"font-family:宋体\">还可用于</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">SNP</span><span style=\"font-family: 宋体\">检测；在序列测定方面，</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">LI-COR4300</span><span style=\"font-family:宋体\">单向测序长度最长可达</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">1200bp</span><span style=\"font-family:宋体\">，准确率高达</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">99%/1000bp</span><span style=\"font-family:宋体\">。</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;text-indent:21px\"><strong><span style=\"font-family:宋体\">功能用途：</span></strong></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;line-height:150%\"><span style=\"font-family:宋体\">主要用于</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">AFLP</span><span style=\"font-family:宋体\">分析、</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">STR</span><span style=\"font-family: 宋体\">分析、</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">SNP</span><span style=\"font-family: 宋体\">筛查、</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">TILLING</span><span style=\"font-family:宋体\">突变分析、</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">DNA</span><span style=\"font-family: 宋体\">测序等。</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;line-height:150%\"><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;line-height:150%\"><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;line-height:150%\"><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;line-height:150%\"><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;line-height:150%\"><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;line-height:150%\"><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"text-align:center;text-indent:28px;line-height:150%\"><strong><span style=\"font-size:19px;line-height:150%;font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">AKTA</span></strong><strong><span style=\"font-size:19px;line-height:150%;font-family:宋体\">蛋白纯化仪</span></strong></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;line-height:150%\"><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;text-indent:0\"><img src=\"/Upload/image/20161116/6361489762853705001970082.jpg\" hspace=\"12\" title=\"clip_image047.jpg\"/></p><p class=\"MsoListParagraph\" style=\"margin-left: 24px;text-indent: 0;line-height: 200%\"><strong><span style=\"font-family:宋体\">仪器型号</span></strong><span style=\"font-family:宋体\">：</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">AKTAPurifier100</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;text-indent:0;line-height:200%\"><strong><span style=\"font-family:宋体\">生产厂家</span></strong><span style=\"font-family: 宋体\">：美国</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">GE</span><span style=\"font-family: 宋体\">公司</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;text-indent:0;line-height:200%\"><strong><span style=\"font-family:宋体\">购置年份</span></strong><span style=\"font-family: 宋体\">：</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">2008</span><span style=\"font-family:宋体\">年</span></p><p style=\"line-height:200%\"><strong><span style=\"font-family:宋体\">存放地点：</span></strong><span style=\"font-family:宋体\">重点实验室</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">406</span></p><p style=\"line-height:200%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p class=\"MsoListParagraph\" style=\"margin-top:10px;margin-right:0;margin-bottom: 10px;margin-left:24px;text-indent:0\"><strong><span style=\"font-family:宋体\">仪器简介：</span></strong></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;line-height:150%\"><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">AKTA purifier</span><span style=\"font-family:宋体\">是一个快速分离肽、核酸、蛋白质和天然产物的全新液相色谱系统，还可用于一些复杂物质的常规检测，如抗生素高分子杂质分析，和传统</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">HPLC</span><span style=\"font-family:宋体\">不一样的是，</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"> AKTA purifier</span><span style=\"font-family:宋体\">兼具了分析和制备能力，可以准确收集图谱上每一个峰作其它研究用途。系统采用标准</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">HPLC</span><span style=\"font-family:宋体\">连接设计，可兼容正相层析柱，系统备有各类反相层析、疏水层析、离子交换、凝胶过滤，亲和层析和高效逆流色谱柱可供选择。</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;text-indent:21px\"><strong><span style=\"font-family:宋体\">功能用途：</span></strong></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family:宋体\">适用各种层析技术，专门分离和纯化各类生物分子，包括天然蛋白质，重组和融合蛋白质、肽、寡核酸、质粒、病毒、抗生素、生物碱等。</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-align:center;text-indent:28px;line-height:150%\"><strong><span style=\"font-size:19px;line-height: 150%;font-family:宋体\">流式细胞仪</span></strong></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;line-height:150%\"><img src=\"/Upload/image/20161116/6361489762858611306955990.jpg\" hspace=\"12\" title=\"clip_image048.jpg\"/></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;line-height:150%\"><strong><span style=\"font-family:宋体\">仪器型号</span></strong><span style=\"font-family:宋体\">：</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">BD Accuri C6</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;text-indent:0;line-height:200%\"><strong><span style=\"font-family:宋体\">生产厂家</span></strong><span style=\"font-family: 宋体\">：美国</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">BD</span><span style=\"font-family: 宋体\">公司</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;text-indent:0;line-height:200%\"><strong><span style=\"font-family:宋体\">购置年份</span></strong><span style=\"font-family: 宋体\">：</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">2014</span><span style=\"font-family:宋体\">年</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;line-height:150%\"><strong><span style=\"font-family:宋体\">存放地点：</span></strong><span style=\"font-family:宋体\">重点实验室</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">415</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-align:center;text-indent:28px;line-height:150%\"><strong><span style=\"font-size:19px;line-height:150%;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></strong></p><p class=\"MsoListParagraph\" style=\"margin-top:10px;margin-right:0;margin-bottom: 10px;margin-left:24px;text-indent:0\"><strong><span style=\"font-family:宋体\">仪器简介：</span></strong></p><p class=\"MsoListParagraph\" style=\"margin-top:10px;margin-right:0;margin-bottom: 0;margin-left:24px;margin-bottom:0;line-height:150%\"><span style=\"font-family:宋体\">预优化的检测器设置，免调电压</span><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">BD Accuri C6</span><span style=\"font-family:宋体\">系统分别配备有一根蓝激光器</span><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\"> (488nm) </span><span style=\"font-family:宋体\">和一根红激光器</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"> (640nm)</span><span style=\"font-family:宋体\">、两个散射光检测器</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"> (FSC</span><span style=\"font-family:宋体\">和</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">SSC) </span><span style=\"font-family:宋体\">以及加载了预优化光学滤光片的四个荧光检测器，以检测例如：</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">FITC</span><span style=\"font-family:宋体\">、</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">PE</span><span style=\"font-family: 宋体\">、</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">PerCP</span><span style=\"font-family:宋体\">和</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">APC</span><span style=\"font-family: 宋体\">等常规荧光染料。紧凑的光路布局、稳定免调的光路设计和经过优化的检测器设置使整套系统更加易于使用。</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;line-height:150%\"><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">BD Accuri C6</span><span style=\"font-family:宋体\">系统数据数字采集具有跨越</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">7</span><span style=\"font-family: 宋体\">个数量级的动态范围</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"> (1600</span><span style=\"font-family:宋体\">万道的数值化数据解析度</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">)</span><span style=\"font-family: 宋体\">，保证所有数据总是有效可用的。另外，在样本收集进程前后，甚至是样本收集进程中，都可进行群体圈定或荧光补偿值的设定。在样本收集完毕后，</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">BD Accuri CFlow®</span><span style=\"font-family:宋体\">软件的自由缩放功能还可以突出任何特定坐标范围内的群体，以便更为精确地识别和圈定分析目标。</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;text-indent:28px\"><strong><span style=\"font-family:宋体\">功能用途：</span></strong></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:28px;line-height:150%\"><span style=\"font-family:宋体\">适用于通过检测</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">DNA</span><span style=\"font-family:宋体\">含量分析细胞周期，细胞表型分析（免疫学和血液学），细胞功能检测，干细胞的鉴定与分离，肿瘤研究等。</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-align:center;text-indent:28px;line-height:150%\"><strong><span style=\"font-size:19px;line-height: 150%;font-family:宋体\">高效液相色谱仪</span></strong></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;line-height:150%\"><img src=\"/Upload/image/20161116/6361489762862040882941898.jpg\" hspace=\"12\" title=\"clip_image049.jpg\"/></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;line-height:150%\"><strong><span style=\"font-family:宋体\">仪器型号</span></strong><span style=\"font-family:宋体\">：</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">Agilent1200 LC</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;text-indent:0;line-height:200%\"><strong><span style=\"font-family:宋体\">生产厂家</span></strong><span style=\"font-family: 宋体\">：美国安捷伦公司</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;text-indent:0;line-height:200%\"><strong><span style=\"font-family:宋体\">购置年份</span></strong><span style=\"font-family: 宋体\">：</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">2010</span><span style=\"font-family:宋体\">年</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;line-height:150%\"><strong><span style=\"font-family:宋体\">存放地点：</span></strong><span style=\"font-family:宋体\">重点实验室</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">406</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p class=\"MsoListParagraph\" style=\"margin-top:10px;margin-right:0;margin-bottom: 10px;margin-left:24px;text-indent:0\"><strong><span style=\"font-family:宋体\">仪器简介：</span></strong></p><p style=\"margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family:宋体\">由储液器、泵、进样器、色谱柱、检测器、记录仪等几部分组成。储液器中的流动相被高压泵打入系统，样品溶液经进样器进入流动相，被流动相截入色谱柱内，由于样品溶液中的各组分在两相中具有不同的分配系数，在两相中作相对运动时，经过反复多次的吸附</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">-</span><span style=\"font-family:宋体\">解吸的分配过程，各组分在移动速度上产生较大的差别，被分离成单个组分依次从柱内流出，通过检测器时，样品浓度被转换成电信号传送到记录仪，数据以图谱形式打印出来。</span></p><p style=\"margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family:宋体\">安捷伦</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">1200</span><span style=\"font-family:宋体\">系列快速高分离液相系统可提供最快分析速度、最高分辨率，同时最大限度地保持系统低压力而设计的。因此，它保留了常规</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"> HPLC </span><span style=\"font-family:宋体\">仪器和方法的耐用性和工作原理。这种独特的设计使</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">1200 RRLC</span><span style=\"font-family:宋体\">成为了一种通用的液相分析流速范围适合的柱尺寸从</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">1 </span><span style=\"font-family:宋体\">到</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">4.6-mm ID, 10 </span><span style=\"font-family:宋体\">到</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"> 300-mm </span><span style=\"font-family:宋体\">柱长，粒度</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">1.5</span><span style=\"font-family: 宋体\">到</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"> 10 µm</span><span style=\"font-family:宋体\">。</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;text-indent:28px\"><strong><span style=\"font-family:宋体\">功能用途：</span></strong></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;text-indent:28px\"><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体\">可用于天然产物（例如药用植物）中化学成分定性定量分析。</span></p><p style=\"line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-align:center;text-indent:28px;line-height:150%\"><strong><span style=\"font-size:19px;line-height: 150%;font-family:宋体\">气相色谱与质谱联用仪</span></strong></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><img src=\"/Upload/image/20161116/6361489762867034123225978.jpg\" hspace=\"12\" title=\"clip_image050.jpg\"/></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;line-height:150%\"><strong><span style=\"font-family:宋体\">仪器型号</span></strong><span style=\"font-family:宋体\">：</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">Agilent 7890A</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;text-indent:0;line-height:200%\"><strong><span style=\"font-family:宋体\">生产厂家</span></strong><span style=\"font-family: 宋体\">：美国安捷伦公司</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;text-indent:0;line-height:200%\"><strong><span style=\"font-family:宋体\">购置年份</span></strong><span style=\"font-family: 宋体\">：</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">2013</span><span style=\"font-family:宋体\">年</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;line-height:150%\"><strong><span style=\"font-family:宋体\">存放地点：</span></strong><span style=\"font-family:宋体\">重点实验室</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">406</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p class=\"MsoListParagraph\" style=\"margin-top:10px;margin-right:0;margin-bottom: 10px;margin-left:24px;text-indent:0\"><strong><span style=\"font-family:宋体\">仪器简介：</span></strong></p><p style=\"margin-left:28px;text-indent:28px;line-height:150%\"><span style=\"font-family:宋体\">本实验测试分析系统是以惰性气体为流动相，以固体吸附剂为固定相。当混合样品进入色谱柱，由于吸附剂对每个组分的吸附力不同，各组分在色谱中的运行速度也就不同。各组分得以在色谱柱中彼此分离，顺序进入检测器中被检测和记录下来。</span></p><p style=\"margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span><span style=\"font-family:宋体\">质谱分析是使样品中各组分在离子源中发生电离，生成不同荷质比的带正电荷的离子，经加速电场的作用，形成离子束，进入质量分析器，再利用电场和磁场使发生相反的速度色散，将它们分别聚焦而得到质谱图，从而确定其质量。</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;text-indent:28px\"><strong><span style=\"font-family:宋体\">功能用途：</span></strong></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family:宋体\">测定分子质量、分子式或分子组成。可用于食品工业（例如酒类、豆瓣、醋等芳香类物质的检测）、农药残留、中药挥发性成分解析。</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-indent:21px;line-height:150%\"><span style=\"font-family: &#39;Times New Roman&#39;,&#39;serif&#39;\">&nbsp;</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:28px;text-align:center;text-indent:28px;line-height:150%\"><strong><span style=\"font-size:19px;line-height: 150%;font-family:宋体\">酶标仪</span></strong></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;line-height:150%\"><img src=\"/Upload/image/20161116/6361489762870386769684468.jpg\" hspace=\"12\" title=\"clip_image051.jpg\"/></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;line-height:150%\"><strong><span style=\"font-family:宋体\">仪器型号</span></strong><span style=\"font-family:宋体\">：</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">Epoch</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;text-indent:0;line-height:200%\"><strong><span style=\"font-family:宋体\">生产厂家</span></strong><span style=\"font-family: 宋体\">：美国伯腾公司</span></p><p class=\"MsoListParagraph\" style=\"margin-left:24px;text-indent:0;line-height:200%\"><strong><span style=\"font-family:宋体\">购置年份</span></strong><span style=\"font-family: 宋体\">：</span><span style=\"font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">2013</span><span style=\"font-family:宋体\">年<